import React from 'react';
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';


const units = [
    "unit",
    "g",
    "kg",
    "l",
    "ml"
];


function NewRecipe() {

    const {register, handleSubmit, errors} = useForm();

    const onSubmit = (data) => {
        console.log(data);
        alert("Form is submitted");
    };

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>

            <label htmlFor={"title"}>Title</label>
            <input className={"form-control"} name={"title"} type={"text"}
                   ref={register({
                       required: true,
                       minLength: {value: 5, message: "* Title is too short"},
                       maxLength: {value: 100, message: "* Title exceeds the maximum characters"}
                   })}/>
            {errors.title && <p>{errors.title.message}</p>}

            <div className={"row"}>
                <div className={"col"}>
                    <label htmlFor={"meal"}>Meal</label>
                    <select className={"form-select"} name={"meal"} defaultValue={"Select..."}
                            ref={register({required: true})}>
                        <option value={"Select..."} disabled>Select...</option>
                        <option>Breakfast</option>
                        <option>Lunch</option>
                        <option>Dinner</option>
                    </select>

                </div>
                <div className={"col"}>
                    <label htmlFor={"difficulty"}>Difficulty</label>
                    <select className={"form-select"} name={"difficulty"}
                            defaultValue={"Select..."} ref={register({required: true})}>
                        <option value={"Select..."} disabled>Select...</option>
                        <option>Easy</option>
                        <option>Medium</option>
                        <option>Hard</option>
                    </select>
                </div>
            </div>

            <label htmlFor={"ingredients"}>Ingredients</label>
            <input className={"form-control"} name={"ingredients"}
                   id={"ingredientDataList"} list={"ingredientDataListOptions"} ref={register({required: true})}
            />
            <datalist id={"ingredientDataListOptions"}>
                <option>Default</option>
            </datalist>

            <div className={"row"}>
                <div className={"col"}>
                    <label htmlFor={"amount"}>Amount</label>
                    <input className={"form-control"} name={"amount"} type={"number"} ref={register({required: true})}/>
                </div>
                <div className={"col"}>
                    <label htmlFor={"unit"}>Unit</label>
                    <select className={"form-select"} name={"unit"}
                            defaultValue={"Select..."} ref={register({required: true})}>
                        <option value={"Select..."} disabled>Select...</option>
                        {units.map((unit, index) => {
                            return (<option key={index}>{unit}</option>)
                        })}
                    </select>
                </div>
            </div>

            <input type={"button"} value={"Add Ingredient"}/>

            <label htmlFor={"steps"}>Steps</label>
            <textarea className={"form-control"} name={"steps"} rows={"5"} ref={register({required: true})}/>

            <input type={"submit"}/>
        </form>
    )
}

export default NewRecipe;
