import React, {useEffect, useState} from "react";
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';
import axios from "axios";

function NewRecipe() {

    const {register, handleSubmit, watch, errors} = useForm();
    const [ingredients, setIngredients] = useState([{
        id: '',
        name: ''
    }]);

    const onSubmit = (data) => {

        const recipe = {
            title: data.title,
            meal: data.meal,
            difficulty: data.difficulty,
            steps: data.steps,
            ingredients: data.ingredients
        };

    };

    function getAllIngredients() {

        axios.get("http://localhost:8080/api/ingredients")
            .then(response => {
                setIngredients(response.data);
                console.log(response.data);
            })
    };

    useEffect(() => {
       getAllIngredients();
    });

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>
            <div className={"row"}>
                <div className={"col"}>
                    <label>Title</label>
                    <input className={"form-control"} name={"title"} ref={register}/>

                    <label>Meal</label>
                    <input className={"form-control"} name={"meal"} ref={register}/>

                    <label>Difficulty</label>
                    <input className={"form-control"} name={"difficulty"} ref={register}/>

                    <label>Ingredients</label>
                    <input className={"form-control"} id={"recipeDataList"} list={"datalistOptions"} placeholder={"Type to search..."}/>
                    <datalist id={"datalistOptions"}>
                        {ingredients.map(ingredient => {
                            return (
                                <option key={ingredient.id} value={ingredient.name}/>
                            )
                        })}
                    </datalist>

                    <div className={"row"}>
                        <div className={"col"}>
                            <label>Amount</label>
                            <input className={"form-control"} name={"amount"} ref={register}/>
                        </div>
                        <div className={"col"}>
                            <label>Unit</label>
                            <input className={"form-control"} name={"unit"} ref={register}/>
                        </div>
                    </div>
                </div>
                <div className={"col"}>
                    <label>Steps</label>
                    <textarea className={"form-control"} rows="16" ref={register}/>
                </div>
            </div>
            <input type={"submit"}/>
        </form>
    )
}

export default NewRecipe;
