import React from "react";
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';
import axios from "axios";

function NewRecipe() {

    const {register, handleSubmit, watch, errors} = useForm();

    const onSubmit = (data) => {

        const recipe = {
            title: data.title,
            meal: data.meal,
            difficulty: data.difficulty,
            steps: data.steps,
            ingredients: data.ingredients
        };

    };

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>
            <div className={"row"}>
                <div className={"col"}>
                    <label>Title</label>
                    <input name={"title"} ref={register}/>
                    <label>Meal</label>
                    <input name={"meal"} ref={register}/>
                    <label>Difficulty</label>
                    <input name={"difficulty"} ref={register}/>
                    <label>Ingredients</label>
                    <input name={"ingredients"} type={"text"} ref={register}/>
                    <div className={"col"}>
                        <label>Amount</label>
                        <input name={"amount"} ref={register}/>
                    </div>
                    <div className={col}>
                        <label>Unit</label>
                        <input name={"unit"} ref={register}/>
                    </div>
                </div>
                <div className={"col"}>
                    <label>Steps</label>
                    <textarea className={"form-control"} rows="10" ref={register}/>
                </div>
            </div>
            <input type={"submit"}/>
        </form>
    )
}

export default NewRecipe;
