import React from 'react';

function Form({errors, handleChange, handleSubmit, values}) {
    return (
        <div>
            <form>
                <div className={"form-group"}>
                    <label htmlFor={"title-input"}>
                        Title *
                        <input
                            type={"text"}
                            className={"form-control"}
                            id={"title-input"}
                            name={"title"}
                            placeholder={"Please enter the title"}
                            onChange={handleChange}
                            required
                        />
                        {errors.title}
                    </label>
                </div>
                <div className={"form-group"}>
                    <label htmlFor={"meal-input"}>
                        Meal *
                        <input
                            type={"select"}
                            className={"form-control"}
                            id={"meal-input"}
                            name={"meal"}
                            onChange={handleChange}

                        />
                    </label>
                </div>
            </form>
        </div>
    )
}

export default Form;
