import React, {useEffect, useState} from "react";
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';
import axios from "axios";

function NewRecipe() {

    const units = [
        "unit",
        "g",
        "kg",
        "l",
        "ml"
    ];
    const meals = [
        "Breakfast",
        "Lunch",
        "Dinner"
    ];
    const difficulties = [
        "Easy",
        "Medium",
        "Hard"
    ];
    const ingredient = {
        id: '',
        name: ''
    };
    const recipe = {
        title: '',
        meal: '',
        difficulty: '',
        steps: '',
        recipeIngredients: ''
    };

    const [ingredientName, setIngredientName] = useState('');
    const [ingredientAmount, setIngredientAmount] = useState('');
    const [ingredientUnit, setIngredientUnit] = useState('');

    const {register, handleSubmit} = useForm();
    const [ingredients, getIngredients] = useState([ingredient]);
    const [ingredientList, setIngredientList] = useState([]);
    const [ingredientId, setIngredientId] = useState(0);

    useEffect(() => {
        getAllIngredients();
    }, []);

    function getAllIngredients() {
        axios.get("http://localhost:8080/api/ingredients")
            .then(response => {
                getIngredients(response.data);
                console.log(response.data);
            })
    }

    function handleOnChangeIngredient(event) {
        const ingredientName = event.target.value;
        setIngredientName(ingredientName);
        console.log(ingredientName);

        let newIngredientId = ingredients.find((ingredient) => ingredient.name === ingredientName);
        setIngredientId(newIngredientId.id);
    }

    function handleAddedIngredient() {
        const ingredientDetail = {
            'id': ingredientId,
            'name': ingredientName,
            'amount': ingredientAmount,
            'unit': ingredientUnit
        };
        console.log(ingredientDetail);
        return setIngredientList(ingredientList => [...ingredientList, ingredientDetail]);
    }

    function onSubmit(data) {
        const recipe = {
            title: data.title,
            meal: data.meal,
            difficulty: data.difficulty,
            steps: data.steps,
            ingredientSet: ingredientList
        };
        console.log(recipe);

        axios.post("http://localhost:8080/api/recipes", recipe)
            .then(response => {
                console.log(response.data);
            }).catch(error => console.log(error));
    }

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>
            <div className={"form-recipe-head"}>
                <label>Title</label>
                <input className={"form-control"} name={"title"} ref={register}/>

                <div className={"row"}>
                    <div className={"col"}>
                        <label>Meal</label>
                        <select className={"form-select"} name={"meal"} ref={register}>
                            <option>Select</option>
                            {meals.map((meal, index) => {
                                return (
                                    <option key={index}>{meal}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div className={"col"}>
                        <label>Difficulty</label>
                        <select className={"form-select"} name={"difficulty"} ref={register}>
                            <option>Select</option>
                            {difficulties.map((difficulty, index) => {
                                return (
                                    <option key={index}>{difficulty}</option>
                                )
                            })}
                        </select>
                    </div>
                </div>

                <label>Ingredients</label>
                {/*<input className={"form-control"} name={"option"} id={"recipeDataList"}*/}
                {/*       list={"recipeDatalistOptions"}*/}
                {/*       placeholder={"Type to search..."} />*/}
                <select className={"form-select"} name={"ingredients"} onChange={handleOnChangeIngredient}>
                    <option>Select</option>
                    {ingredients.map(ingredient => {
                        return (
                            <option key={ingredient.id}>{ingredient.name}</option>
                        );
                    })}
                </select>

                <div className={"row"}>
                    <div className={"col"}>
                        <label>Amount</label>
                        <input className={"form-control"} name={"amount"} onChange={e => setIngredientAmount(e.target.value)} ref={register}/>
                    </div>
                    <div className={"col"}>
                        <label>Unit</label>
                        <input className={"form-control"} name={"unit"} id={"unitDataList"}
                               list={"unitDatalistOptions"} onChange={e => setIngredientUnit(e.target.value)} ref={register}/>
                        <datalist id={"unitDatalistOptions"}>
                            {units.map((unit, index) => {
                                return (
                                    <option key={index} value={unit}/>
                                )
                            })}
                        </datalist>
                    </div>
                </div>
                <div className={"row"}>
                    <input className={"btn btn-primary"} type={"button"} value={"Add"} onClick={handleAddedIngredient}/>
                </div>
                <div className={"row"}>
                    <ul className={"list-group list-group-flush"} ref={register}>
                        {ingredientList.map((ingredient, index) => {
                            return (
                                <li className={"list-group-item"} key={index}>
                                    {ingredient.name} - {ingredient.amount} {ingredient.unit}
                                </li>
                            )
                        })}
                    </ul>
                </div>
            </div>
            <div className={"form-recipe-bottom"}>
                <label>Steps</label>
                <textarea className={"form-control"} name={"steps"} rows="16" ref={register}/>
                <input type={"submit"}/>
            </div>
        </form>
    )
}

export default NewRecipe;
