import React, {useEffect, useState} from "react";
import Select from "react-select";
import {Controller, useForm} from 'react-hook-form';
import './css/Recipe.css';
import axios from "axios";


function Recipe({validate}) {

    const units = [
        "unit",
        "g",
        "kg",
        "l",
        "ml"
    ];
    const meals = [
        "Breakfast",
        "Lunch",
        "Dinner"
    ];
    const difficulties = [
        "Easy",
        "Medium",
        "Hard"
    ];
    const ingredient = {
        id: '',
        name: ''
    };

    const {control, register, handleSubmit, errors} = useForm();

    const [ingredients, getIngredients] = useState([ingredient]);
    const [ingredientId, setIngredientId] = useState(0);
    const [ingredientName, setIngredientName] = useState('');
    const [ingredientAmount, setIngredientAmount] = useState('');
    const [ingredientUnit, setIngredientUnit] = useState('');
    const [ingredientList, setIngredientList] = useState([]);


    useEffect(() => {
        getAllIngredients();
    }, []);

    function getAllIngredients() {
        axios.get("http://localhost:8080/api/ingredients")
            .then(response => {
                getIngredients(response.data);
            })
    }

    function handleOnChangeIngredient(event) {
        const ingredientName = event.target.value;
        setIngredientName(ingredientName);

        let ingredientId = ingredients.find((ingredient) => ingredient.name === ingredientName);
        setIngredientId(ingredientId.id);
    }

    function handleOnChangeAmount(event) {
        const ingredientAmount = event.target.value;
        setIngredientAmount(ingredientAmount);
    }

    function handleAddedIngredient() {

        if (ingredientAmount === "") {
            let message = "Please pick an amount needed";
            console.log(message);
        }

        const ingredientDetail = {
            'id': ingredientId,
            'name': ingredientName,
            'amount': ingredientAmount,
            'unit': ingredientUnit
        };
        console.log(ingredientDetail);
        return setIngredientList(ingredientList => [...ingredientList, ingredientDetail]);
    }

    function onSubmit(data) {
        const recipe = {
            title: data.title,
            meal: data.meal,
            difficulty: data.difficulty,
            steps: data.steps,
            ingredientSet: ingredientList
        };
        console.log(recipe);

        axios.post("http://localhost:8080/api/recipes", recipe)
            .then(response => {
                console.log(response.data);
            }).catch(error => console.log(error))
            .then(refreshPage);
    }

    function refreshPage() {
        window.location.reload();
    }

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>
            <div className={"form-recipe-head"}>
                <label htmlFor={"title"}>Title</label>
                <input className={"form-control"} name={"title"}
                       ref={register({
                           required: "Please give a title for your recipe",
                           maxLength: {
                               value: 50,
                               message: "Your title has exceeded the maximum length"
                           }
                       })}/>
                {errors.title && <p>{errors.title.message}</p>}
                <div className={"row"}>
                    <div className={"col"}>
                        <label htmlFor={"meal"}>Meal</label>
                        {/*<select className={"form-select"} name={"meal"}
                                defaultValue={"Select"}
                                ref={register({
                                    required: true,
                                    pattern: {
                                        message: "Please select a meal"
                                    }
                                })}>
                            <option value={"Select"} disabled>Select</option>
                            {meals.map((meal, index) => {
                                return (
                                    <option key={index}>{meal}</option>
                                )
                            })}
                        </select>*/}
                        <Controller
                            name={"meal"}
                            control={control}
                            rules={{required: true}}
                            render={({field}) => <Select
                                {...field}
                                options={[
                                    {value: "Breakfast", label: "Breakfast"},
                                    {value: "Lunch", label: "Lunch"},
                                    {value: "Dinner", label: "Dinner"}
                                ]}
                            />}
                        />
                        {errors.meal && <p>Meal is required</p>}
                    </div>
                    <div className={"col"}>
                        <label htmlFor={"difficulty"}>Difficulty</label>
                        <Controller
                            name={"difficulty"}
                            control={control}
                            rules={{required: true}}
                            render={({field}) => <Select
                                {...field}
                                options={[
                                    {value: "Easy", label: "Easy"},
                                    {value: "Medium", label: "Medium"},
                                    {value: "Hard", label: "Hard"}
                                ]}
                            />}
                        />
                        {errors.difficulty && <p>Difficulty is required</p>}
                    </div>
                </div>
                <label htmlFor={"ingredients"}>Ingredients</label>
                <select className={"form-select"} name={"ingredients"}
                        defaultValue={"Select"} onChange={handleOnChangeIngredient}
                        ref={register({
                            required: true,
                            pattern: {
                                message: "Please select your ingredients"
                            }
                        })}>
                    <option value={"Select"} disabled>Select</option>
                    {ingredients.map(ingredient => {
                        return (
                            <option key={ingredient.id}>{ingredient.name}</option>
                        );
                    })}
                </select>
                <div className={"row"}>
                    <div className={"col"}>
                        <label>Amount</label>
                        <input className={"form-control"} name={"amount"}
                               onChange={handleOnChangeAmount}
                               ref={register({
                                   required: true
                               })}/>
                        {errors.amount && <p>Please add the amount needed</p>}
                    </div>
                    <div className={"col"}>
                        <label>Unit</label>
                        <input className={"form-control"} name={"unit"} id={"unitDataList"}
                               list={"unitDatalistOptions"} onChange={e => setIngredientUnit(e.target.value)}
                               ref={register({
                                   required: true
                               })}/>
                        <datalist id={"unitDatalistOptions"}>
                            {units.map((unit, index) => {
                                return (
                                    <option key={index} value={unit}/>
                                )
                            })}
                        </datalist>
                        {errors.unit && <p>Please choose a unit</p>}
                    </div>
                </div>
                <div className={"row"}>
                    <input className={"btn btn-primary"} type={"button"} value={"Add"} onClick={handleAddedIngredient}/>
                </div>
                <div className={"row"}>
                    <ul className={"list-group list-group-flush"} ref={register}>
                        {ingredientList.map((ingredient, index) => {
                            return (
                                <li className={"list-group-item"} key={index}>
                                    {ingredient.name} - {ingredient.amount} {ingredient.unit}
                                </li>
                            )
                        })}
                    </ul>
                </div>
            </div>
            <div className={"form-recipe-bottom"}>
                <label>Steps</label>
                <textarea className={"form-control"} name={"steps"} rows="8" ref={register({
                    required: "Please add the instruction for your recipe"
                })}/>
                {errors.steps && <p>{errors.steps.message}</p>}
                <input type={"submit"}/>
            </div>

        </form>
    )
}

export default Recipe;
