import React, {useEffect, useState} from 'react';
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';


function NewRecipe() {

    const {register, handleSubmit, errors} = useForm();

    const onSubmit = (data) => {
        console.log(data);
        alert("Form is submitted");
    };

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>

            <label htmlFor={"title"}>Title</label>
            <input className={"form-control"} name={"title"} type={"text"} ref={register({required: true})}/>

            <div className={"row"}>
                <div className={"col"}>
                    <label htmlFor={"meal"}>Meal</label>
                    <input className={"form-control"} name={"meal"} type={"select"} ref={register({required: true})}/>

                </div>
                <div className={"col"}>
                    <label>Difficulty</label>
                    <input/>
                </div>
            </div>

            <label>Ingredients</label>
            <input/>

            <div className={"row"}>
                <div className={"col"}>
                    <label>Amount</label>
                    <input/>
                </div>
                <div className={"col"}>
                    <label>Unit</label>
                    <input/>
                </div>
            </div>

            <input type={"button"} value={"Add Ingredient"}/>

            <label>Steps</label>
            <input/>

            <input type={"submit"}/>
        </form>
    )
}

export default NewRecipe;
