import React, {useEffect, useState} from "react";
import Baked from "./img/lm4rceme60edquisbnwb.jpeg";
import {useParams} from "react-router-dom";
import './css/RecipeDetail.css';
import axios from "axios";


function RecipeDetail() {

    const recipeId = useParams();

    const recipe = {
        id: '',
        title: '',
        meal: '',
        difficulty: '',
        ingredientSet: ''
    };

    const [state, setState] = useState(recipe);

    useEffect(() => {
        getRecipeById(recipeId.id);
    }, []);

    const getRecipeById = (recipeId) => {
        axios.get("http://localhost:8080/api/recipes/" + recipeId)
            .then(response => {
                if (response.data != null) {
                    setState({
                        title: response.data.title,
                        meal: response.data.meal,
                        difficulty: response.data.difficulty,
                        ingredientSet: response.data.ingredientSet
                    });
                    console.log(response.data);
                }
            });
    };

    return (
        <div className={"container"} style={{marginTop: "60px", width: "60%"}}>
            <div className={"row"}>
                <div className={"col-4"}>
                    <img className={"img-fluid"} src={Baked} alt={"..."}/>
                    <div className={"col"}>
                        <h5>Ingredients: </h5>
                        <ul>
                            {state.ingredientSet}
                        </ul>
                    </div>
                </div>
                <div className={"col-8"}>
                    <h3>{state.title}</h3>
                    <div className={"row"}>
                        <div className={"col"}>
                            <h5>Meal: </h5>
                        </div>
                        <div className={"col"}>
                            <h5>Difficulty: </h5>
                        </div>
                    </div>
                    <div className={"col"}><h5>Steps: </h5></div>
                </div>
            </div>
        </div>
    )
}

export default RecipeDetail;
