import React, {useEffect, useState} from "react";
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';
import axios from "axios";

function NewRecipe() {

    const {register, handleSubmit} = useForm();
    const [ingredients, setIngredients] = useState([{
        id: '',
        name: ''
    }]);
    const units = [
        "unit",
        "g",
        "kg",
        "l",
        "ml"
    ];

    useEffect(() => {
        getAllIngredients();
    }, []);

    const onSubmit = (data) => {

        const recipe = {
            title: data.title,
            meal: data.meal,
            difficulty: data.difficulty,
            steps: data.steps,
            ingredients: data.ingredients
        };

    };

    const onChange = (data) => {

    };

    function getAllIngredients() {

        axios.get("http://localhost:8080/api/ingredients")
            .then(response => {
                setIngredients(response.data);
                console.log(response.data);
            })
    }

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>
            <label>Title</label>
            <input className={"form-control"} name={"title"} ref={register}/>

            <label>Ingredients</label>
            <input className={"form-control"} name={"ingredients"} id={"recipeDataList"} list={"recipeDatalistOptions"}
                   placeholder={"Type to search..."} ref={register}/>
            <datalist id={"recipeDatalistOptions"}>
                {ingredients.map(ingredient => {
                    return (
                        <option key={ingredient.id} value={ingredient.name}/>
                    )
                })}
            </datalist>

            <div className={"row"}>
                <div className={"col"}>
                    <label>Amount</label>
                    <input className={"form-control"} name={"amount"} ref={register}/>
                </div>
                <div className={"col"}>
                    <label>Unit</label>
                    <input className={"form-control"} name={"unit"} id={"unitDataList"} list={"unitDatalistOptions"}
                           ref={register}/>
                    <datalist id={"unitDatalistOptions"}>
                        {units.map((unit, index) => {
                            return (
                                <option key={index} value={unit}/>
                            )
                        })}
                    </datalist>
                </div>
            </div>
            <label>Meal</label>
            <div className={"input-group"}>
                <div className={"form-check"}>
                    <input className={"form-check-input"} type={"radio"} id={"breakfastRadioDefault"} checked
                           name={"meal"} onChange={onChange} ref={register}/>
                    <label className={"form-check-label"}>Breakfast</label>
                </div>
                <div className={"form-check"}>
                    <label className={"form-check-label"}>Lunch</label>
                    <input className={"form-check-input"} type={"radio"} id={"lunchRadio"} name={"meal"}
                           onChange={onChange} ref={register}/>
                </div>
                <div className={"form-check"}>
                    <label className={"form-check-label"}>Dinner</label>
                    <input className={"form-check-input"} type={"radio"} id={"dinnerRadio"} name={"meal"}
                           onChange={onChange} ref={register}/>
                </div>
                <div className={"form-check"}>
                    <label className={"form-check-label"}>Desert</label>
                    <input className={"form-check-input"} type={"radio"} id={"desertRadio"} name={"meal"}
                           onChange={onChange} ref={register}/>
                </div>
            </div>
            <label>Difficulty</label>
            <div className={"input-group"}>
                <div className={"form-check"}>
                    <input className={"form-check-input"} type={"radio"} id={"easyRadioDefault"} checked
                           name={"difficulty"} onChange={onChange} ref={register}/>
                    <label className={"form-check-label"}>Easy</label>
                </div>
                <div className={"form-check"}>
                    <label className={"form-check-label"}>Medium</label>
                    <input className={"form-check-input"} type={"radio"} id={"mediumRadio"} name={"difficulty"}
                           onChange={onChange} ref={register}/>
                </div>
                <div className={"form-check"}>
                    <label className={"form-check-label"}>Hard</label>
                    <input className={"form-check-input"} type={"radio"} id={"hardRadio"} name={"difficulty"}
                           onChange={onChange} ref={register}/>
                </div>
            </div>
            <label>Steps</label>
            <textarea className={"form-control"} name={"steps"} rows="16" ref={register}/>
            <input type={"submit"}/>
        </form>
    )
}

export default NewRecipe;
