import React, {useEffect, useState} from "react";
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';
import axios from "axios";

function NewRecipe() {

    const {data, setData} = useState('');
    const {register, handleSubmit} = useForm();
    const [ingredients, setIngredients] = useState([{
        id: '',
        name: ''
    }]);
    const units = [
        "unit",
        "g",
        "kg",
        "l",
        "ml"
    ];

    const meals = [
        "Breakfast",
        "Lunch",
        "Dinner"
    ];

    const difficulties = [
        "Easy",
        "Medium",
        "Hard"
    ];

    useEffect(() => {
        getAllIngredients();
    }, []);

    const onSubmit = (data) => {

        const recipe = {
            title: data.title,
            meal: data.meal,
            difficulty: data.difficulty,
            steps: data.steps,
            ingredients: data.ingredients
        };

    };

    function handleChange(event) {
        setData(event.target.value);
    }

    function getAllIngredients() {

        axios.get("http://localhost:8080/api/ingredients")
            .then(response => {
                setIngredients(response.data);
                console.log(response.data);
            })
    }

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>
            <div className={"form-recipe-head"} onChange={handleChange}>
                <label>Title</label>
                <input className={"form-control"} name={"title"} ref={register}/>

                <div className={"row"}>
                    <div className={"col"}>
                        <label>Meal</label>
                        <select className={"form-select"} name={"meal"} ref={register}>
                            <option>Select</option>
                            {meals.map((meal, index) => {
                                return (
                                    <option key={index}>{meal}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div className={"col"}>
                        <label>Difficulty</label>
                        <select className={"form-select"} name={"difficulty"} ref={register}>
                            <option>Select</option>
                            {difficulties.map((difficulty, index) => {
                                return (
                                    <option key={index}>{difficulty}</option>
                                )
                            })}
                        </select>
                    </div>
                </div>

                <label>Ingredients</label>
                <input className={"form-control"} name={"ingredients"} id={"recipeDataList"}
                       list={"recipeDatalistOptions"}
                       placeholder={"Type to search..."} ref={register}/>
                <datalist id={"recipeDatalistOptions"}>
                    {ingredients.map(ingredient => {
                        return (
                            <option key={ingredient.id} value={ingredient.name}/>
                        )
                    })}
                </datalist>

                <div className={"row"}>
                    <div className={"col"}>
                        <label>Amount</label>
                        <input className={"form-control"} name={"amount"} ref={register}/>
                    </div>
                    <div className={"col"}>
                        <label>Unit</label>
                        <input className={"form-control"} name={"unit"} id={"unitDataList"} list={"unitDatalistOptions"}
                               ref={register}/>
                        <datalist id={"unitDatalistOptions"}>
                            {units.map((unit, index) => {
                                return (
                                    <option key={index} value={unit}/>
                                )
                            })}
                        </datalist>
                    </div>
                </div>
                <div className={"row"}>
                    <input className={"btn btn-primary"} type={"button"} value={"Add"}/>
                </div>
            </div>
            <div className={"form-recipe-bottom"}>

                <label>Steps</label>
                <textarea className={"form-control"} name={"steps"} rows="16" ref={register}/>
                <input type={"submit"}/>
            </div>
        </form>
    )
}

export default NewRecipe;
