import React, {useEffect, useState} from "react";
import Baked from "./img/lm4rceme60edquisbnwb.jpeg";
import {useParams} from "react-router-dom";
import './css/RecipeDetail.css';
import axios from "axios";


function RecipeDetail() {

    const recipeIngredient = {
        recipeIngredientId: {
            ingredientId: '',
            recipeId: ''
        },
        amount: '',
        unit: ''
    };

    const recipe = {
        id: '',
        title: '',
        meal: '',
        difficulty: '',
        recipeIngredients: [recipeIngredient],
        steps: ''
    };

    const ingredient = {
        id: '',
        name: ''
    };

    const [state, setState] = useState(recipe);
    const [ingredientName, setIngredientName] = useState('');
    const recipeId = useParams();

    useEffect(() => {
        getRecipeById(recipeId.id);
    }, []);

    const getRecipeById = (id) => {
        axios.get("http://localhost:8080/api/recipes/" + id)
            .then(response => {
                if (response.data != null) {
                    setState({
                        title: response.data.title,
                        meal: response.data.meal,
                        difficulty: response.data.difficulty,
                        recipeIngredients: response.data.recipeIngredients,
                        steps: response.data.steps
                    });
                    console.log(response.data);
                }
            });
    };

    const getIngredientById = (id) => {
        axios.get("http://localhost:8080/api/ingredients/" + id)
            .then(response => {
                if (response.data != null) {
                    setIngredientName(response.data.name);
                    console.log(response.data.name);
                }
            });
    };

    return (
        <div className={"container"} style={{marginTop: "60px", width: "80%"}}>
            <div className={"row"}>
                <div className={"col-4"}>
                    <img className={"img-fluid"} src={Baked} alt={"..."}/>
                    <div className={"col"}>
                        <br/>
                        <h5>Ingredients: </h5>
                        {state.recipeIngredients.map((recipeIngredient, index) => {
                            return (
                                <li key={index}>({recipeIngredient.amount} {recipeIngredient.unit})</li>
                            )
                        })}
                    </div>
                </div>
                <div className={"col-8"}>
                    <h3>{state.title}</h3>
                    <div className={"row"}>
                        <div className={"col"}>
                            <h6>Meal: {state.meal} </h6>
                        </div>
                        <div className={"col"}>
                            <h6>Difficulty: {state.difficulty} </h6>
                        </div>
                    </div>
                    <br/>
                    <div className={"col"}>
                        <h5>Steps: </h5>
                        <p>{state.steps}</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default RecipeDetail;
