import React, {useEffect, useState} from "react";
import {useForm} from 'react-hook-form';
import './css/NewRecipe.css';
import axios from "axios";

function NewRecipe() {

    const units = [
        "unit",
        "g",
        "kg",
        "l",
        "ml"
    ];
    const meals = [
        "Breakfast",
        "Lunch",
        "Dinner"
    ];
    const difficulties = [
        "Easy",
        "Medium",
        "Hard"
    ];

    const [ingredientName, setIngredientName] = useState('');
    const [ingredientAmount, setIngredientAmount] = useState('');
    const [ingredientUnit, setIngredientUnit] = useState('');

    const {register, handleSubmit} = useForm();
    const [ingredients, getIngredients] = useState([{
        id: '',
        name: ''
    }]);
    const [ingredientList, setIngredientList] = useState([]);

    useEffect(() => {
        getAllIngredients();
    }, []);

    function onSubmit(data) {
        const recipe = {
            title: data.title,
            meal: data.meal,
            difficulty: data.difficulty,
            steps: data.steps,
            ingredients: data.ingredients
        };
    }

    function getAllIngredients() {
        axios.get("http://localhost:8080/api/ingredients")
            .then(response => {
                getIngredients(response.data);
                console.log(response.data);
            })
    }

    function handleOnChangeIngredient() {
        const ingredientDetail = {
            'name': ingredientName,
            'amount': ingredientAmount,
            'unit': ingredientUnit
        };
        return ingredientDetail;
    }

    function handleAddedIngredient() {
        const newIngredient = handleOnChangeIngredient();
        // ingredientList.push(newIngredient);
        console.log(newIngredient);
        setIngredientList(ingredientList => [...ingredientList, newIngredient]);
        console.log(ingredientList);
    }

    function displayAddedIngredient(props) {
        const ingredients = props;
        if (ingredients.length > 0) {
            return (
                ingredients.map ((ingredient, index) => {
                    return (
                        <div key={index}>
                            {ingredient.name}
                        </div>
                    )
                })
            )
        } else {
            return (
                <p>No ingredients yet</p>
            )
        }
    }

    return (
        <form className={"form-recipe"} onSubmit={handleSubmit(onSubmit)}>
            <div className={"form-recipe-head"}>
                <label>Title</label>
                <input className={"form-control"} name={"title"} ref={register}/>

                <div className={"row"}>
                    <div className={"col"}>
                        <label>Meal</label>
                        <select className={"form-select"} name={"meal"} ref={register}>
                            <option>Select</option>
                            {meals.map((meal, index) => {
                                return (
                                    <option key={index}>{meal}</option>
                                )
                            })}
                        </select>
                    </div>
                    <div className={"col"}>
                        <label>Difficulty</label>
                        <select className={"form-select"} name={"difficulty"} ref={register}>
                            <option>Select</option>
                            {difficulties.map((difficulty, index) => {
                                return (
                                    <option key={index}>{difficulty}</option>
                                )
                            })}
                        </select>
                    </div>
                </div>

                <label>Ingredients</label>
                <input className={"form-control"} name={"ingredients"} id={"recipeDataList"}
                       list={"recipeDatalistOptions"}
                       placeholder={"Type to search..."} onChange={e => setIngredientName(e.target.value)}/>
                <datalist id={"recipeDatalistOptions"}>
                    {ingredients.map(ingredient => {
                        return (
                            <option key={ingredient.id} value={ingredient.name}/>
                        )
                    })}
                </datalist>

                <div className={"row"}>
                    <div className={"col"}>
                        <label>Amount</label>
                        <input className={"form-control"} name={"amount"} onChange={e => setIngredientAmount(e.target.value)}/>
                    </div>
                    <div className={"col"}>
                        <label>Unit</label>
                        <input className={"form-control"} name={"unit"} id={"unitDataList"}
                               list={"unitDatalistOptions"} onChange={e => setIngredientUnit(e.target.value)}/>
                        <datalist id={"unitDatalistOptions"}>
                            {units.map((unit, index) => {
                                return (
                                    <option key={index} value={unit}/>
                                )
                            })}
                        </datalist>
                    </div>
                </div>
                <div className={"row"}>
                    <input className={"btn btn-primary"} type={"button"} value={"Add"} onClick={handleAddedIngredient}/>
                </div>
                <div className={"row"}>

                </div>
            </div>
            <div className={"form-recipe-bottom"}>

                <label>Steps</label>
                <textarea className={"form-control"} name={"steps"} rows="16" ref={register}/>
                <input type={"submit"}/>
            </div>
        </form>
    )
}

export default NewRecipe;
