import React, {useEffect, useState} from "react";
import Baked from "./img/lm4rceme60edquisbnwb.jpeg";
import {useParams} from "react-router-dom";
import './css/RecipeDetail.css';
import axios from "axios";


function RecipeDetail() {

    const recipeIngredient = {
        recipeIngredientId: {
            ingredientId: '',
            recipeId: ''
        },
        amount: '',
        unit: '',
    };

    const recipe = {
        id: '',
        title: '',
        meal: '',
        difficulty: '',
        recipeIngredients: [recipeIngredient],
        steps: ''
    };

    const recipeId = useParams();
    const [recipeState, setRecipeState] = useState(recipe);
    const [ingredientName, setIngredientName] = useState('');
    const [ingredientList, setIngredientList] = useState([]);


    useEffect(() => {
        getRecipeById(recipeId.id);
        console.log(recipeState.recipeIngredients);
    }, []);

    const getRecipeById = (id) => {
        axios.get("http://localhost:8080/api/recipes/" + id)
            .then(response => {
                if (response.data != null) {
                    console.log(response.data);
                    setRecipeState({
                        title: response.data.title,
                        meal: response.data.meal,
                        difficulty: response.data.difficulty,
                        recipeIngredients: response.data.recipeIngredients,
                        steps: response.data.steps
                    });
                    console.log(response.data.recipeIngredients);
                }
            });
    };

    // const getIngredients = () => {
    //     let i;
    //     for (i = 0; i < recipeState.recipeIngredients.length; i++) {
    //         console.log(recipeState);
    //         const id = recipeState.recipeIngredients[i].recipeIngredientId.ingredientId;
    //         console.log(id);
    //         axios.get("http://localhost:8080/api/ingredients/" + id)
    //             .then(response => setIngredientName(response.data.name));
    //         console.log(ingredientName);
    //
    //         const ingredient = {
    //             id: id,
    //             name: ingredientName,
    //             amount: recipeState.recipeIngredients[i].amount,
    //             unit: recipeState.recipeIngredients[i].unit,
    //         };
    //
    //         console.log(ingredient);
    //         setIngredientList(ingredientList => [...ingredientList, ingredient]);
    //     }
    // };


    return (
        <div className={"container"} style={{marginTop: "60px", width: "80%"}}>
            <div className={"row"}>
                <div className={"col-4"}>
                    <img className={"img-fluid"} src={Baked} alt={"..."}/>
                    <div className={"col"}>
                        <br/>
                        <h5>Ingredients: </h5>
                        {ingredientList.map((ingredient, index) => {
                            return (
                                <li key={index}>
                                    {ingredient.name} ({ingredient.amount} {recipeIngredient.unit})
                                </li>
                            )
                        })}
                    </div>
                </div>
                <div className={"col-8"}>
                    <h3>{recipeState.title}</h3>
                    <div className={"row"}>
                        <div className={"col"}>
                            <h6>Meal: {recipeState.meal} </h6>
                        </div>
                        <div className={"col"}>
                            <h6>Difficulty: {recipeState.difficulty} </h6>
                        </div>
                    </div>
                    <br/>
                    <div className={"col"}>
                        <h5>Steps: </h5>
                        <p>{recipeState.steps}</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default RecipeDetail;
